import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { setCart, addCart, delCart } from '../redux/action';

const Cart = () => {
  const state = useSelector((state) => state.handleCart);
  const dispatch = useDispatch();

  useEffect(() => {
    // Fetch cart data from your JSON server
    fetch('http://localhost:3000/cartdetails')
      .then((response) => response.json())
      .then((data) => {
        dispatch(setCart(data));
      })
      .catch((error) => {
        console.error('Error fetching data: ', error);
      });
  }, [dispatch]);

  // ... Your CartItem, handleAdd, handleDel, getTotalPrice, and checkoutButton functions remain the same ...

  return (
    <div>
      {state.length === 0 ? (
        <div className="px-4 my-5 bg-light rounded-3 py-5">
          <div className="container py-4">
            <div className="row">
              <h3>Your Cart is Empty</h3>
              <p>Start shopping now!</p>
            </div>
          </div>
        </div>
      ) : (
        <>
          {state.map((item) => (
            <CartItem
              key={item.product_id}
              item={item}
              handleAdd={handleAdd}
              handleDel={handleDel}
            />
          ))}
          {checkoutButton}
          <p>Total: ${getTotalPrice()}</p>
        </>
          )}
    </div>
  );
};

export default Cart;
