import React from 'react';
import {useSelector, useDispatch} from 'react-redux'
import { NavLink } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { addCart, delCart } from '../redux/action';


const Cart = () => {
  const state = useSelector((state) => state.handleCart);
    const dispatch = useDispatch()

    const handleAdd = (item) => {
        dispatch(addCart(item))
    }
    const handleDel = (item) => {
        console.log("working")
        dispatch(delCart(item))
    }

    const [cartData, setCartData] = useState({});
    const [loading, setLoading] = useState(true);

    

    useEffect(() => {
        // Use the URL of your JSON data
        fetch('http://localhost:3000/cartdetails')
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                setCartData(data[data.length]);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching data: ', error);
                setLoading(false);
            });
    }, []);


    const getTotalPrice = () => {
      return state.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
    };

    
    // Assuming cartData is the JSON object representing the cart
function addToCart(product) {
  // Check if the product is already in the cart
  const existingItem = cartData.cart.find(
    (item) => item.product_id === product.product_id
  );

  if (existingItem) {
    // If the product is in the cart, update its quantity
    existingItem.quantity += 1;
  } else {
    // If the product is not in the cart, add it
    cartData.cart.push({
      product_id: product.product_id,
      name: product.name,
      "ur;": product.url,
      price: product.price,
      quantity: 1,
    });
  }

  // Recalculate the total or other cart-related information
  Cart();
}


    const emptyCart = () => {
        return(
            <div className="px-4 my-5 bg-light rounded-3 py-5">
                <div className="container py-4">
                    <div className="row">
                        <h3>Your Cart is Empty</h3>
                    </div>
                </div>
            </div>
        )
    }
    const cartItems = (rest) => {
        console.log(rest, 'gggggggggg')
        return(
            <>
                
                <div className="px-4 my-5 bg-light rounded-3 py-5">
                <div className="container py-4">
                    <div className="row justify-content-center">
                        <div className="col-md-4">
                            <img src={rest.url} alt={rest.foodName} height="200px" width="180px" />
                        </div>
                        <div className="col-md-4">
                            <h3>{rest.foodName}</h3>
                            <p className="lead fw-bold">
                                {rest.qty} X ${rest.price} = ${rest.qty * rest.price}
                            </p>
                            <button className="btn btn-outline-dark me-4" onClick={()=>handleDel(rest)}>
                                <i className="fa fa-minus"></i>
                            </button>
                            <button className="btn btn-outline-dark" onClick={()=> handleAdd(rest)}>
                                <i className="fa fa-plus"></i>
                            </button>
                        
                        </div>
                    </div>
                </div>
            </div>
            
            </>
            
        )

    }
    const buttons = () => {
        return(
            <>
                <div className="container">
                    <div className="row">
                        <NavLink to="/checkout" className="btn btn-outline-dark mb-5 w-25 mx-auto">
                            Proceed to Checkout
                        </NavLink>
                    </div>
                </div>
            </>
        )
    }

    return (
        <div>
            {state.length === 0 && emptyCart()}
            {state.length !== 0 && state.map(cartItems)}
            {state.length !== 0 && buttons()}
            <p>Total: ${getTotalPrice()}</p>
        </div>
    );
  }

export default Cart;
