import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addCart, delCart } from '../redux/action';
 

const CartItem = ({ item, handleAdd, handleDel }) => {
  // No changes here, your CartItem component remains the same
};

const Cart = () => {
  const state = useSelector((state) => state.handleCart);
  const dispatch = useDispatch();

  useEffect(() => {
    // Load cart data from local storage when the component mounts
    const localCartData = JSON.parse(localStorage.getItem('anuCart')) || [];
    // Dispatch an action to set the cart state
    dispatch({ type: 'SET_CART', payload: localCartData });
  }, [dispatch]);

  useEffect(() => {
    // Save the cart data to local storage whenever it changes
    localStorage.setItem('anuCart', JSON.stringify(state));
  }, [state]);

  const handleAdd = (item) => {
    // Dispatch an action to add an item to the cart
    dispatch(addCart(item));
  };

  const handleDel = (item) => {
    // Dispatch an action to remove an item from the cart
    dispatch(delCart(item));
  };

  const getTotalPrice = () => {
    // Calculate the total price using the state (cart) obtained from Redux
    return state.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
  };

  const checkoutButton = (
    <div className="container">
      <div className="row">
        {state.length > 0 ? (
          <NavLink to="/checkout" className="btn btn-outline-dark mb-5 w-25 mx-auto">
            Proceed to Checkout
          </NavLink>
        ) : (
          <p>Your cart is empty. Add items to continue.</p>
        )}
      </div>
    </div>
  );

  return (
    <div>
      {state.length === 0 ? (
        <div className="px-4 my-5 bg-light rounded-3 py-5">
          <div className="container py-4">
            <div className="row">
              <h3>Your Cart is Empty</h3>
              <p>Start shopping now!</p>
            </div>
          </div>
        </div>
      ) : (
        <>
          {state.map((item) => (
            <CartItem
              key={item.product_id}
              item={item}
              handleAdd={handleAdd}
              handleDel={handleDel}
            />
          ))}
          {checkoutButton}
          <p>Total: ${getTotalPrice()}</p>
        </>
      )}
    </div>
  );
};

export default Cart;
