import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addCart, delCart } from '../redux/action';

const Cart = () => {
    const state = useSelector((state) => state.handleCart);
    const dispatch = useDispatch();

    const handleAdd = (item) => {
        dispatch(addCart(item));
    };

    const handleDel = (item) => {
        dispatch(delCart(item));
    };

    const clearCart = () => {
        // Dispatch an action to clear the cart
    };

    const emptyCart = (
        <div className="px-4 my-5 bg-light rounded-3 py-5">
            <div className="container py-4">
                <div className="row">
                    <h3>Your Cart is Empty</h3>
                </div>
            </div>
        </div>
    );

    const cartItems = (item) => (
        <div key={item.product_id} className="px-4 my-5 bg-light rounded-3 py-5">
            <div className="container py-4">
                <div className="row justify-content-center">
                    <div className="col-md-4">
                        <img src={item.url} alt={item.foodName} height="200px" width="180px" />
                    </div>
                    <div className="col-md-4">
                        <h3>{item.foodName}</h3>
                        <p className="lead fw-bold">
                            {item.qty} X ${item.price} = ${(item.qty * item.price).toFixed(2)}
                        </p>
                        <button className="btn btn-outline-dark me-4" onClick={() => handleDel(item)}>
                            <i className="fa fa-minus"></i>
                        </button>
                        <button className="btn btn-outline-dark" onClick={() => handleAdd(item)}>
                            <i className="fa fa-plus"></i>
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div>
            {state.length === 0 ? (
                emptyCart
            ) : (
                <div>
                    {state.map(cartItems)}
                    <div className="container mt-4">
                        <div className="row justify-content-end">
                            <div className="col-md-4">
                                <div className="bg-light rounded-3 py-3">
                                    <h4>Subtotal: ${getSubtotal()}</h4>
                                    <h4>Tax (10%): ${(0.1 * getSubtotal()).toFixed(2)}</h4>
                                    <h3>Total: ${getTotal()}</h3>
                                    <button className="btn btn-danger" onClick={clearCart}>
                                        Clear Cart
                                    </button>
                                    <NavLink
                                        to="/checkout"
                                        className={`btn btn-outline-dark mb-3 w-100 ${
                                            state.length === 0 ? 'disabled' : ''
                                        }`}
                                    >
                                        Proceed to Checkout
                                    </NavLink>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );

    function getSubtotal() {
        return state.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
    }

    function getTotal() {
        const subtotal = getSubtotal();
        return (parseFloat(subtotal) + 0.1 * parseFloat(subtotal)).toFixed(2);
    }
};

export default Cart;
