import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addCart, delCart } from '../redux/action';

const Cart = () => {
    const dispatch = useDispatch();
    const [cartData, setCartData] = useState([]);
    const [loading, setLoading] = useState(true);

    const handleAdd = (item) => {
        // Update the cart on the server and locally
        fetch(`http://localhost:3001/cart/1`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                product_id: item.product_id,
                quantity: 1,
            }),
        })
            .then((response) => response.json())
            .then((data) => {
                setCartData(data.cart);
            });

        dispatch(addCart(item));
    };

    const handleDel = (item) => {
        // Update the cart on the server and locally
        fetch(`http://localhost:3001/cart/1`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                product_id: item.product_id,
                quantity: -1,
            }),
        })
            .then((response) => response.json())
            .then((data) => {
                setCartData(data.cart);
            });

        dispatch(delCart(item));
    };

    useEffect(() => {
        // Fetch cart data from the server
        fetch('http://localhost:3001/cart/1')
            .then((response) => response.json())
            .then((data) => {
                setCartData(data.cart);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching data: ', error);
                setLoading(false);
            });
    }, []);

    const emptyCart = (
        <div className="px-4 my-5 bg-light rounded-3 py-5">
            <div className="container py-4">
                <div className="row">
                    <h3>Your Cart is Empty</h3>
                </div>
            </div>
        </div>
    );

    const cartItems = (item) => (
        <div key={item.product_id} className="px-4 my-5 bg-light rounded-3 py-5">
            <div className="container py-4">
                <div className="row justify-content-center">
                    <div className="col-md-4">
                        <img src={item.url} alt={item.foodName} height="200px" width="180px" />
                    </div>
                    <div className="col-md-4">
                        <h3>{item.foodName}</h3>
                        <p className="lead fw-bold">
                            {item.qty} X ${item.price} = ${(item.qty * item.price).toFixed(2)}
                        </p>
                        <button className="btn btn-outline-dark me-4" onClick={() => handleDel(item)}>
                            <i className="fa fa-minus"></i>
                        </button>
                        <button className="btn btn-outline-dark" onClick={() => handleAdd(item)}>
                            <i className="fa fa-plus"></i>
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <>
                    {cartData.length === 0 ? (
                        emptyCart
                    ) : (
                        <div>
                            {cartData.map(cartItems)}
                            <div className="container mt-4">
                                <div className="row justify-content-end">
                                    <div className="col-md-4">
                                        <div className="bg-light rounded-3 py-3">
                                            <h4>Subtotal: ${getSubtotal()}</h4>
                                            <h4>Tax (10%): ${(0.1 * getSubtotal()).toFixed(2)}</h4>
                                            <h3>Total: ${getTotal()}</h3>
                                            <NavLink
                                                to="/checkout"
                                                className={`btn btn-outline-dark mb-3 w-100`}
                                            >
                                                Proceed to Checkout
                                            </NavLink>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}
                </>
            )}
        </div>
    );

    function getSubtotal() {
        return cartData.reduce((total, item) => total + item.quantity * item.price, 0).toFixed(2);
    }

    function getTotal() {
        const subtotal = getSubtotal();
        return (parseFloat(subtotal) + 0.1 * parseFloat(subtotal)).toFixed(2);
    }
};

export default Cart;
