const cart = [];

const handleCart = (state = cart, action) => {
  const orderData= (cart)=>{
    fetch('http://localhost:3000/cartdetails', {
        method: 'DELETE',
    })
        .then(() => {
          //  Cart data deleted successfully
            console.log("deleted");
        })
        .catch((error) => {
            console.error('Error deleting cart data: ', error);
        });

    // fetch("http://localhost:3000/cartdetails", {
    //   method: "POST",
    //   headers: { "content-type": "application/json" },
    //   body: JSON.stringify(cart),
    // })
    //   .then((res) => {
    //     console.log(123)
    //   //  toast.success("Checkout successfully.");
    //   //  navigate("/order");
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //    // toast.error("Failed: " + err.message);
    //   });
  }
  const rest = action.payload;
  switch (action.type) {
    case "ADDITEM":
      //  check if Rest is Already Exist
      const exist = state.find((x) => x.id === rest.id);
      if (exist) {
        // Increase the Quantity
         return state.map((x) =>
          x.id === rest.id ? { ...x, qty: x.qty + 1 } : x
        );
        orderData(state);
        return state;
      } else {
        const rest = action.payload;
        const gg= [
          ...state,
          {
            ...rest,
            qty: 1,
          },
        ];
        orderData(gg);
        return gg;
      }
      orderData(state);

    case "DELTITEM":
      const exist1 = state.find((x) => x.id === rest.id);
      console.log("delete");
      if (exist1.qty === 1) {
        return state.filter((x) => x.id !== exist1.id);
      } else {
        console.log("cart si th");
        return state.map((x) =>
          x.id === rest.id ? { ...x, qty: x.qty - 1 } : x
        );
      }

    default:
      return state;
  }
};
export default handleCart;
