// actions.js
import axios from 'axios';

// Action to add an item to the cart
export const addCart = (item) => {
  return async (dispatch) => {
    try {
      // Make the request to your JSON server to update the cart
      await axios.put(' http://localhost:3000/cartdetails', updatedCartData);

      // Dispatch the action to update the cart in the Redux store
      dispatch({ type: 'ADD_TO_CART', payload: item });
    } catch (error) {
      // Handle errors here
    }
  };
};

// Action to remove an item from the cart
export const delCart = (item) => {
  return async (dispatch) => {
    try {
      // Make the request to your JSON server to update the cart
      await axios.put(' http://localhost:3000/cartdetails', updatedCartData);

      // Dispatch the action to update the cart in the Redux store
      dispatch({ type: 'REMOVE_FROM_CART', payload: item });
    } catch (error) {
      // Handle errors here
    }
  };
};
