import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { setCart, addCart, delCart } from '../redux/action';

const Cart = () => {
  const state = useSelector((state) => state.handleCart);
  const dispatch = useDispatch();

  useEffect(() => {
    // Fetch cart data from your JSON server
    fetch('http://localhost:3000/cartdetails')
      .then((response) => response.json())
      .then((data) => {
        // Update the Redux store with the fetched cart data
        dispatch(setCart(data));
      })
      .catch((error) => {
        console.error('Error fetching data: ', error);
      });
  }, [dispatch]);

  const handleAdd = (item) => {
    dispatch(addCart(item));
  };

  const handleDel = (item) => {
    dispatch(delCart(item));
  };

  const getTotalPrice = () => {
    return state.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
  };

  const checkoutButton = (
    <div className="container">
      <div className="row">
        {state.length > 0 ? (
          <NavLink to="/checkout" className="btn btn-outline-dark mb-5 w-25 mx-auto">
            Proceed to Checkout
          </NavLink>
        ) : (
          <p>Your cart is empty. Add items to continue.</p>
        )}
      </div>
    </div>
  );

  return (
    <div>
      {state.length === 0 ? (
        <div className="px-4 my-5 bg-light rounded-3 py-5">
          <div className="container py-4">
            <div className="row">
              <h3>Your Cart is Empty</h3>
              <p>Start shopping now!</p>
            </div>
          </div>
        </div>
      ) : (
        <>
          {state.map((item) => (
            <CartItem
              key={item.product_id}
              item={item}
              handleAdd={handleAdd}
              handleDel={handleDel}
            />
          ))}
          {checkoutButton}
          <p>Total: ${getTotalPrice()}</p>
        </>
      )}
    </div>
  );
};

export default Cart;
