import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addCart, delCart } from '../redux/action';

const Cart = () => {
  const state = useSelector((state) => state.handleCart);
  const dispatch = useDispatch();

  const handleAdd = (item) => {
    dispatch(addCart(item));
    addToCart(item);
  };

  const handleDel = (item) => {
    dispatch(delCart(item));
    removeFromCart(item);
  };

  const [cartData, setCartData] = useState([]);

  useEffect(() => {
    // Fetch cart data from the server
    fetchCartData();
  }, []);

  const fetchCartData = () => {
    fetch('http://localhost:3000/cart')
      .then((response) => response.json())
      .then((data) => {
        setCartData(data);
      })
      .catch((error) => {
        console.error('Error fetching data: ', error);
      });
  };

  const addToCart = (product) => {
    // Your existing addToCart logic
    // ...

    // Update the cart data on the server
    updateCartData(updatedCartData);
  };

  const removeFromCart = (product) => {
    // Your existing removeFromCart logic
    // ...

    // Update the cart data on the server
    updateCartData(updatedCartData);
  };

  const updateCartData = (updatedData) => {
    fetch('http://localhost:3000/cartdetails', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedData),
    })
      .then(() => {
        setCartData(updatedData);
      })
      .catch((error) => {
        console.error('Error updating cart data: ', error);
      });
  };

  const getTotalPrice = () => {
    return state.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
  };

  const emptyCart = () => {
    return (
      <div className="px-4 my-5 bg-light rounded-3 py-5">
        <div className="container py-4">
          <div className="row">
            <h3>Your Cart is Empty</h3>
          </div>
        </div>
      </div>
    );
  };

  const cartItems = (rest) => {
    // Your existing cartItems rendering logic
    // ...
  };

  const buttons = () => {
    return (
      <div className="container">
        <div className="row">
          <NavLink to="/checkout" className="btn btn-outline-dark mb-5 w-25 mx-auto">
            Proceed to Checkout
          </NavLink>
        </div>
      </div>
    );
  };

  return (
    <div>
      {state.length === 0 ? emptyCart() : state.map(cartItems)}
      {state.length !== 0 && buttons()}
      <p>Total: ${getTotalPrice()}</p>
    </div>
  );
};

export default Cart;
