import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addCart, delCart } from '../redux/action';

const Cart = () => {
  const state = useSelector((state) => state.handleCart);
  const dispatch = useDispatch();

  const handleAdd = (item) => {
    dispatch(addCart(item));
    addToCart(item); // Update the cart data on the server
  };

  const handleDel = (item) => {
    dispatch(delCart(item));
    removeFromCart(item); // Update the cart data on the server
  };

  const [cartData, setCartData] = useState([]);
  const [, setLoading] = useState(true);

  useEffect(() => {
    // Fetch cart data from your JSON server
    fetch('http://localhost:3000/cart')
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setCartData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data: ', error);
        setLoading(false);
      });
  }, []);

  // Add item to the cart on your JSON server
  function addToCart(product) {
    // Check if the product is already in the cart
    const existingItem = cartData.find((item) => item.product_id === product.product_id);

    if (existingItem) {
      // If the product is in the cart, update its quantity
      existingItem.quantity += 1;
    } else {
      // If the product is not in the cart, add it
      cartData.push({
        product_id: product.product_id,
        name: product.name,
        url: product.url,
        price: product.price,
        quantity: 1,
        totalPrice: product.price,
      });
    }

    // Update the cart data on your JSON server
    // fetch(' http://localhost:3000/cartdetails', {
    //   method: 'PUT', // Use the appropriate HTTP method for updating data
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(cartData),
    // });
  }

  // Remove item from the cart on your JSON server
  function removeFromCart(product) {
    // Find the index of the product in the cart
    const productIndex = cartData.findIndex((item) => item.product_id === product.product_id);

    if (productIndex !== -1) {
      // If the product is found, decrease its quantity or remove it from the cart
      if  (cartData[productIndex].quantity > 1) 
      {
        cartData[productIndex].quantity -= 1;
      } else {
        cartData.splice(productIndex, 1);
      }

      // Update the cart data on your JSON server
    //   fetch(' http://localhost:3000/cartdetails', {
    //     method: 'PUT', // Use the appropriate HTTP method for updating data
    //     headers: {
    //       'Content-Type': 'application/json',
    //     },
    //     body: JSON.stringify(cartData),
    //   });
    }
  

  // ...the rest of your component code
};

const getTotalPrice = () => {
    return state.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
  };



  const emptyCart = () => {
    return (
      <div className="px-4 my-5 bg-light rounded-3 py-5">
        <div className="container py-4">
          <div className="row">
            <h3>Your Cart is Empty</h3>
          </div>
        </div>
      </div>
    );
  };

  const cartItems = (rest) => {
    console.log(rest, 'gggggggggg')
    return(
        <>
            
            <div className="px-4 my-5 bg-light rounded-3 py-5">
            <div className="container py-4">
                <div className="row justify-content-center">
                    <div className="col-md-4">
                        <img src={rest.url} alt={rest.foodName} height="200px" width="180px" />
                    </div>
                    <div className="col-md-4">
                        <h3>{rest.foodName}</h3>
                        <p className="lead fw-bold">
                            {rest.qty} X ${rest.price} = ${rest.qty * rest.price}
                        </p>
                        <button className="btn btn-outline-dark me-4" onClick={()=>handleDel(rest)}>
                            <i className="fa fa-minus"></i>
                        </button>
                        <button className="btn btn-outline-dark" onClick={()=> handleAdd(rest)}>
                            <i className="fa fa-plus"></i>
                        </button>
                    
                    </div>
                </div>
            </div>
        </div>
        
        </>
        
    )

}

  const buttons = () => {
    return (
      <div className="container">
        <div className="row">
          <NavLink to="/checkout" className="btn btn-outline-dark mb-5 w-25 mx-auto">
            Proceed to Checkout
          </NavLink>
        </div>
      </div>
    );
  };

  return (
    <div>
      {state.length === 0 && emptyCart()}
      {state.length !== 0 && state.map(cartItems)}
      {state.length !== 0 && buttons()}
      <p>Total: ${getTotalPrice()}</p>
    </div>
 );
};


export default Cart;
