import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addCart, delCart } from '../redux/action';

const Cart = () => {
  const dispatch = useDispatch();
  const state = useSelector((state) => state.handleCart);

  const [cartData, setCartData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch cart data from your API or local JSON file
    fetch('http://localhost:3000/cartdetails')
      .then((response) => response.json())
      .then((data) => {
        setCartData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data: ', error);
        setLoading(false);
      });
  }, []);

  const addToCart = (product) => {
    const updatedCart = [...state];
    const existingItemIndex = updatedCart.findIndex(
      (item) => item.product_id === product.product_id
    );

    if (existingItemIndex !== -1) {
      // If the product is in the cart, update its quantity
      updatedCart[existingItemIndex].qty += 1;
    } else {
      // If the product is not in the cart, add it
      updatedCart.push({ ...product, qty: 1 });
    }

    dispatch(addCart(updatedCart));
  };

  const removeFromCart = (product) => {
    const updatedCart = state.filter(
      (item) => item.product_id !== product.product_id
    );

    dispatch(delCart(updatedCart));
  };

  const getTotalPrice = () => {
    return state.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
  };

  const renderCartItems = () => {
    if (state.length === 0) {
      return (
        <div className="px-4 my-5 bg-light rounded-3 py-5">
          <div className="container py-4">
            <div className="row">
              <h3>Your Cart is Empty</h3>
            </div>
          </div>
        </div>
      );
    }

    return (
      <>
        {state.map((item) => (
          <div key={item.product_id} className="px-4 my-5 bg-light rounded-3 py-5">
            <div className="container py-4">
              <div className="row justify-content-center">
                <div className="col-md-4">
                  <img src={item.url} alt={item.name} height="200px" width="180px" />
                </div>
                <div className="col-md-4">
                  <h3>{item.name}</h3>
                  <p className="lead fw-bold">
                    {item.qty} X ${item.price} = ${(item.qty * item.price).toFixed(2)}
                  </p>
                  <button className="btn btn-outline-dark me-4" onClick={() => removeFromCart(item)}>
                    <i className="fa fa-minus"></i>
                  </button>
                  <button className="btn btn-outline-dark" onClick={() => addToCart(item)}>
                    <i className="fa fa-plus"></i>
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
        <div>
            {state.length === 0 && emptyCart()}
            {state.length !== 0 && state.map(cartItems)}
            {state.length !== 0 && buttons()}
            <p>Total: ${getTotalPrice()}</p>
        </div>
        
      </>
    );
  };

  return <div>{renderCartItems()}</div>;
};

export default Cart;
