import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addCart, delCart } from '../redux/action';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    // Load cart items from local storage when the component mounts
    const storedCart = JSON.parse(localStorage.getItem('anuCart')) || [];
    setCartItems(storedCart);
  }, []);

  const saveCartToLocalStorage = () => {
    // Save the current cart items to local storage whenever they change
    localStorage.setItem('anuCart', JSON.stringify(cartItems));
  };

  const handleAdd = (item) => {
    const updatedCart = [...cartItems];
    const index = updatedCart.findIndex((cartItem) => cartItem.product_id === item.product_id);

    if (index !== -1) {
      updatedCart[index].qty += 1;
    } else {
      item.qty = 1;
      updatedCart.push(item);
    }

    setCartItems(updatedCart);
  };

  const handleDel = (item) => {
    const updatedCart = [...cartItems];
    const index = updatedCart.findIndex((cartItem) => cartItem.product_id === item.product_id);

    if (index !== -1) {
      if (updatedCart[index].qty > 1) {
        updatedCart[index].qty -= 1;
      } else {
        updatedCart.splice(index, 1);
      }
      setCartItems(updatedCart);
    }
  };

  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
  };

  const checkoutButton = (
    <div className="container">
      <div className="row">
        {cartItems.length > 0 ? (
          <NavLink to="/checkout" className="btn btn-outline-dark mb-5 w-25 mx-auto">
            Proceed to Checkout
          </NavLink>
        ) : (
          <p>Your cart is empty. Add items to continue.</p>
        )}
      </div>
    </div>
  );

  return (
    <div>
      {cartItems.length === 0 ? (
        <div className="px-4 my-5 bg-light rounded-3 py-5">
          <div className="container py-4">
            <div className="row">
              <h3>Your Cart is Empty</h3>
              <p>Start shopping now!</p>
            </div>
          </div>
        </div>
      ) : (
        <>
          {cartItems.map((item) => (
            <CartItem key={item.product_id} item={item} handleAdd={handleAdd} handleDel={handleDel} />
          ))}
          {checkoutButton}
          <p>Total: ${getTotalPrice()}</p>
        </>
      )}
    </div>
  );
};

export default Cart;
