import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addToCart, removeFromCart } from './actions';
import { NavLink } from 'react-router-dom';

const Cart = () => {
    const cart = useSelector((state) => state.cart);
    const dispatch = useDispatch();

    const handleAdd = (item) => {
        // Dispatch Redux action to add to cart
        dispatch(addToCart(item));

        // Update the cart on the server
        updateCartOnServer(item, 1);
    };

    const handleDel = (item) => {
        // Dispatch Redux action to remove from cart
        dispatch(removeFromCart(item));

        // Update the cart on the server
        updateCartOnServer(item, -1);
    };

    const [cartData, setCartData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch cart data from the server
        fetch('http://localhost:3001/cartdetails')
            .then((response) => response.json())
            .then((data) => {
                setCartData(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching data: ', error);
                setLoading(false);
            });
    }, []);

    const getTotalPrice = () => {
        return cart.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
    };

    const updateCartOnServer = (item, quantityChange) => {
        const updatedCart = cartData.map((cartItem) => {
            if (cartItem.product_id === item.product_id) {
                return {
                    ...cartItem,
                    quantity: cartItem.quantity + quantityChange,
                };
            }
            return cartItem;
        });

        // Update the cart on the server
        fetch('http://localhost:3001/cartdetails', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedCart),
        })
            .then(() => {
                // Cart on the server is updated
            })
            .catch((error) => {
                console.error('Error updating cart on the server: ', error);
            });
    };

    const emptyCart = (
        <div className="px-4 my-5 bg-light rounded-3 py-5">
            <div className="container py-4">
                <div className="row">
                    <h3>Your Cart is Empty</h3>
                </div>
            </div>
        </div>
    );

    const cartItems = (rest) => (
        <div key={rest.product_id} className="px-4 my-5 bg-light rounded-3 py-5">
            <div className="container py-4">
                <div className="row justify-content-center">
                    <div className="col-md-4">
                        <img src={rest.url} alt={rest.foodName} height="200px" width="180px" />
                    </div>
                    <div className="col-md-4">
                        <h3>{rest.foodName}</h3>
                        <p className="lead fw-bold">
                            {rest.qty} X ${rest.price} = ${(rest.qty * rest.price).toFixed(2)}
                        </p>
                        <button className="btn btn-outline-dark me-4" onClick={() => handleDel(rest)}>
                            <i className="fa fa-minus"></i>
                        </button>
                        <button className="btn btn-outline-dark" onClick={() => handleAdd(rest)}>
                            <i className="fa fa-plus"></i>
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <>
                    {cartData.length === 0 ? (
                        emptyCart
                    ) : (
                        <div>
                            {cartData.map(cartItems)}
                            <div className="container mt-4">
                                <div className="row justify-content-end">
                                    <div className="col-md-4">
                                        <div className="bg-light rounded-3 py-3">
                                            <h4>Subtotal: ${getTotalPrice()}</h4>
                                            <h4>Tax (10%): ${(0.1 * getTotalPrice()).toFixed(2)}</h4>
                                            <h3>Total: ${(parseFloat(getTotalPrice()) * 1.1).toFixed(2)}</h3>
                                            <NavLink to="/checkout" className="btn btn-outline-dark mb-3 w-100">
                                                Proceed to Checkout
                                            </NavLink>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}
                </>
            )}
        </div>
    );
};

export default Cart;
