import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Login = () => {
    const [ username, usernameupdate] = useState('');
    const [ password, passwordupdate] = useState('');

    const usenavigate=useNavigate()


    const ProcessdLogin=(e)=>{
     e.preventDefault(); 
     if(validate()){
        ///implentation
        // console.log('proceed');
        fetch("http://localhost:3000/user/"+username).then((res)=>{
            return res.json();
        }).then((resp)=>{
            console.log(resp)
            if(Object.keys(resp).length===0){
             toast.error('Please Enter valid username');   
            }else{
                if(resp.password===password){
                    toast.success('success');
                      usenavigate('/')
                }else{
                    toast.error('Please Enter valid credentials'); 
                }
            }
        }).catch((err)=>{
            toast.error('Login Failed due to :' +err.message);
        });
     }  
    }
    const validate=()=>{
        let result=true;
        if(username ==='' || username ===null){
            result=false;
            toast.warning('Please Enter Username');
        }
        if(password ==='' || password ===null){
            result=false;
            toast.warning('Please Enter Password');
        }
        return result;
    }
    return (
        <div className="row">
            <div className="offset-lg-3 col-lg-6">
                <form onSubmit={ProcessdLogin} className="container">
                    <div className="card">
                    <div className="card-header">
                        <h2>User Login</h2>
                    </div>
                    <div className="card-body">
                        <div className="form-group">
                            <label>User Name <span className="errmsg">*</span></label>
                            <input value={username} onChange={e=>usernameupdate(e.target.value)} className="form-control"></input>
                        </div>
                        <div className="form-group">
                            <label> Password <span className="errmsg">*</span></label>
                            <input type="password" value={password} onChange={e=>passwordupdate(e.target.value)} className="form-control"></input>
                        </div>
                    </div>
                    <div className="card-footer">
                       <button type="submit" className="btn btn-primary mb-3 ms-2">Login</button> 
                       <Link className="btn btn-success mb-3 ms-2" to={'/register'}>New User</Link>
                    </div>
                    </div>
                </form>

            </div>
        </div>
    );
}

export default Login;
