import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const processLogin = (e) => {
    e.preventDefault();
    if (validate()) {
      fetchUserData(username, password);
    }
  };

  const validate = () => {
    let result = true;
    if (!username || username.trim() === '') {
      result = false;
      toast.warning('Please enter a username');
    }
    if (!password || password.trim() === '') {
      result = false;
      toast.warning('Please enter a password');
    }
    return result;
  };

  const fetchUserData = (username, password) => {
    fetch("http://localhost:3000/user/" + username)
      .then((res) => res.json())
      .then((resp) => {
        if (Object.keys(resp).length === 0) {
          toast.error('Please enter a valid username');
        } else {
          if (resp.password === password) {
            toast.success('Login successful');
            sessionStorage.setItem('username', username);
            navigate('/');
          } else {
            toast.error('Please enter valid credentials');
          }
        }
      })
      .catch((err) => {
        toast.error('Login failed due to: ' + err.message);
      });
  };

  return (
    <div className="row">
      <div className="offset-lg-3 col-lg-6">
        <form onSubmit={processLogin} className="container">
          <div className="card">
            <div className="card-header">
              <h2>User Login</h2>
            </div>
            <div className="card-body">
              <div className="form-group">
                <label>User Name <span className="errmsg">*</span></label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="form-control"
                />
              </div>
              <div className="form-group">
                <label>Password <span className="errmsg">*</span></label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="form-control"
                />
              </div>
            </div>
            <div className="card-footer">
              <button type="submit" className="btn btn-primary mb-3 ms-2">
                Login
              </button>
              <Link className="btn btn-success mb-3 ms-2" to={'/sign-up'}>
                New User
              </Link>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
