import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';

const Cart = () => {
  const [cartData, setCartData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch cart data from the JSON Server
    fetch('http://localhost:3000/cart')
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setCartData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching cart data: ', error);
        setLoading(false);
      });
  }, []);

  const handleAdd = (item) => {
    // You can implement the add functionality if needed
    // Here, you can make a POST request to add an item to the cart
  }

  const handleDel = (item) => {
    // You can implement the delete functionality if needed
    // Here, you can make a DELETE request to remove an item from the cart
  }

  const emptyCart = () => (
    <div className="px-4 my-5 bg-light rounded-3 py-5">
      <div className="container py-4">
        <div className="row">
          <h3>Your Cart is Empty</h3>
        </div>
      </div>
    </div>
  );

  const cartItems = (item) => (
    <div key={item.id} className="px-4 my-5 bg-light rounded-3 py-5">
      <div className="container py-4">
        <div className="row justify-content-center">
          <div className="col-md-4">
            <img src={item.url} alt={item.foodName} height="200px" width="180px" />
          </div>
          <div className="col-md-4">
            <h3>{item.foodName}</h3>
            <p className="lead fw-bold">
              {item.qty} X ${item.price} = ${item.qty * item.price}
            </p>
            <button className="btn btn-outline-dark me-4" onClick={() => handleDel(item)}>
              <i className="fa fa-minus"></i>
            </button>
            <button className="btn btn-outline-dark" onClick={() => handleAdd(item)}>
              <i className="fa fa-plus"></i>
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  const buttons = () => (
    <div className="container">
      <div className="row">
        <NavLink to="/checkout" className="btn btn-outline-dark mb-5 w-25 mx-auto">
          Proceed to Checkout
        </NavLink>
      </div>
    </div>
  );

  return (
    <div>
      {loading && <p>Loading cart data...</p>}
      {!loading && cartData.length === 0 && emptyCart()}
      {!loading && cartData.length !== 0 && cartData.map(cartItems)}
      {!loading && cartData.length !== 0 && buttons()}
    </div>
  );
};

export default Cart;
