import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { addCart, delCart } from '../redux/action';

const CartItem = ({ item, handleAdd, handleDel }) => (
    <div key={item.product_id} className="px-4 my-5 bg-light rounded-3 py-5">
        <div className="container py-4">
            <div className="row justify-content-center">
                <div className="col-md-4">
                    <img src={item.url} alt={item.foodName} height="200px" width="180px" />
                </div>
                <div className="col-md-4">
                    <h3>{item.foodName}</h3>
                    <p className="lead fw-bold">
                        {item.qty} X ${item.price} = ${(item.qty * item.price).toFixed(2)}
                    </p>
                    <button className="btn btn-outline-dark me-4" onClick={() => handleDel(item)}>
                        <i className="fa fa-minus"></i>
                    </button>
                    <button className="btn btn-outlineDark" onClick={() => handleAdd(item)}>
                        <i className="fa fa-plus"></i>
                    </button>
                </div>
            </div>
        </div>
    </div>
);

const getLocalCartData = () => {
    let localCartData = localStorage.getItem("anuCart");
    if (localCartData == []) {
        return [];
    } else {
        return JSON.parse(localCartData)
    }
    
};
const Cart = () => {
    const state = useSelector((state) => state.handleCart);
    const dispatch = useDispatch();

    // to add the data in localStorage
    useEffect(() => {
        getLocalCartData.setItem("anuCart", JSON.stringify(state));
    }, [state]);

    const handleAdd = (item) => {
        dispatch(addCart(item));
    };

    const handleDel = (item) => {
        dispatch(delCart(item));
    };

    const getTotalPrice = () => {
        return state.reduce((total, item) => total + item.qty * item.price, 0).toFixed(2);
    };

    const checkoutButton = (
        <div className="container">
            <div className="row">
                {state.length > 0 ? (
                    <NavLink to="/checkout" className="btn btn-outline-dark mb-5 w-25 mx-auto">
                        Proceed to Checkout
                    </NavLink>
                ) : (
                    <p>Your cart is empty. Add items to continue.</p>
                )}
            </div>
        </div>
    );

    return (
        <div>
            {state.length === 0 ? (
                <div className="px-4 my-5 bg-light rounded-3 py-5">
                    <div className="container py-4">
                        <div className="row">
                            <h3>Your Cart is Empty</h3>
                            <p>Start shopping now!</p>
                        </div>
                    </div>
                </div>
            ) : (
                <>
                    {state.map((item) => (
                        <CartItem
                            key={item.product_id}
                            item={item}
                            handleAdd={handleAdd}
                            handleDel={handleDel}
                        />
                    ))}
                    {checkoutButton}
                    <p>Total: ${getTotalPrice()}</p>
                </>
            )}
        </div>
    );
};

export default Cart;
